import React, { useState, useEffect, useRef } from 'react';
import locomotiveScroll from "locomotive-scroll";
import 'bootstrap/dist/css/bootstrap.min.css';
import  'bootstrap/dist/css/bootstrap-grid.css'
import './assets/sass/app.scss'
import Header from './presentor/header'
import ScrollDirection from './presentor/ScrollDirection'
import TextScramble from './presentor/TextScramble'
import Party from  './presentor/Party'
import BackGround from './presentor/BackGround';


const App = () => {
  const scrollRef = useRef();

  useEffect(() => {
    const scroll = new locomotiveScroll({
      el: scrollRef.current,
      smooth: true
    });
  });

  return (
    <>

        {/* <div className="scroll" ref={scrollRef}>
          <div className="container-fluid">






          </div>
        </div> */}
        <div className="container-fluid">
          <section>
          <BackGround/>

          <header className="c-header">
            <Header/>
          </header>

          <div className="header_hidden"></div>
          <section style={{height:"500px"}}>
              <div className="row">
                <div className="col-sm-5">
                <TextScramble/>
                </div>
                <div className="col-sm-5">
                  <Party/>
                </div>
              </div>
            </section>
          </section>
          <div className="scroll" ref={scrollRef}>
            <h1 data-scroll data-scroll-speed="3" data-scroll-position="top">
              Locomotive Scroll in React
            </h1>
            <h2
              data-scroll
              data-scroll-speed="2"
              data-scroll-position="top"
              data-scroll-direction="horizontal"
            >
              Ima go sideways
            </h2>
          </div>
        </div>

    </>
  );
};

export default App;
